CREATE TABLE trails (
    "id" INTEGER PRIMARY KEY GENERATED BY DEFAULT AS IDENTITY,
    "sessionId" VARCHAR(36),
    "mobile" BOOLEAN NOT NULL,
    "sessionTime" integer,
    "lat" NUMERIC,
    "lng" NUMERIC,
    "buttonPressed" VARCHAR(32),
    "redHoverTime" INTEGER,
    "redHoverAmount" INTEGER,
    "blueHoverTime" INTEGER,
    "blueHoverAmount" INTEGER,
    "yellowHoverTime" INTEGER,
    "yellowHoverAmount" INTEGER,
    "oneHoverTime" INTEGER,
    "oneHoverAmount" INTEGER,
    "twoHoverTime" INTEGER,
    "twoHoverAmount" INTEGER,
    "threeHoverTime" INTEGER,
    "threeHoverAmount" INTEGER,
    "metaLog" TEXT
);